<?php// No direct access to this filedefined('_JEXEC') or die('Restricted access'); // import Joomla view libraryjimport('joomla.application.component.view'); /** * InTheNews View */class InTheNewsViewClip extends JView{        /**         * display method of Clip view         * @return void         */        public function display($tpl = null)         {                // get the Data                $form = $this->get('Form');                $item = $this->get('Item');				$isNew = ($this->item->id == 0);				                // Check for errors.                if (count($errors = $this->get('Errors')))                 {                        JError::raiseError(500, implode('<br />', $errors));                        return false;                }                // Disable main menu				JRequest::setVar('hidemainmenu', true);				// Toolbar                JToolBarHelper::title($isNew ? JText::_('New Press Hit')                                             : JText::_('Edit Press Hit'));				JToolBarHelper::apply('clip.apply');				JToolBarHelper::save('clip.save');				JToolBarHelper::save2new('clip.save2new');				JToolBarHelper::cancel('clip.cancel', $isNew ? 'Action Canceled' : 'No Changes');				                // Assign the Data                $this->form = $form;				$this->item = $item;                 // Display the template                parent::display($tpl);        } }